import React, { Component } from 'react';
import axios from 'axios';

class Createcom extends Component {

    state ={
        
    }

    componentDidMount(){
        axios.post('http://localhost:4000/category/',{
        UserName: this.state.UserName,
        Comment: this.state.Comment,
    })
    .then(res=>{
        console.log(res, 'Comment added!');
    })
    .catch(error =>{
        console.log(error, 'Comment not added!')
    });
    this.setState({
        UserName: '',
        Comment: '',
    })
    }
    render() {
        return (
            <div >
               <div className="form-group">
               <textarea className="form-control" rows={1}  
                            placeholder ="Enter UserName" 
                            value={this.state.UserName}
                            onChange={this.state.handleUserName}/>
                </div>

                <div className="form-group">
                <textarea className="form-control" rows={3}  
                            placeholder ="Enter Comment" 
                            value={this.state.Comment}
                            onChange={this.state.handleComment}/>
                </div>

                <div className="form-group">
                        <input type="submit" value="Post"
                                 className="btn btn-primary" 
                                 onClick={this.onSubmit}
                                 aria-hidden="true"/>
                </div>
            </div>
        );
    }
}

export default Createcom;
