import React, { Component } from 'react';

class OneNews extends Component {
    constructor(props) {
        super(props);
        this.state={News: []}
    }
    componentDidMount(){
        axios.get('http://localhost:4000/news/2')
        .then(res =>{
            this.setState({News: res.data.data})
        })
        .catch((error)=>{
                console.log("Error" + error)        
            
        })
    }
    render() {
        const {News} =this.state;
        return (
            <div>
      <div className="container">
        <div className="row">
        
            {News.map((item, key) =>{
                const {title, content, short_intro, image, date_created, author_name} = item;
                return(
          <div className="col-lg-8" key={key}>
            <h1 className="mt-4">{title}</h1>
            <p className="lead">
              by
              <a href="#">{author_name}</a>
            </p>
            <hr />
            <p>{date_created}</p>
            <hr />
            <img className="img-fluid rounded" src={image} alt="" />
            <hr />
            <p className="lead">{short_intro}</p> <hr />
            <p className="lead">{content}</p> <hr />
            
            </div>
                )

            })}
            
        </div>   </div>   </div>
        );
    }
}

export default OneNews;
