import React, { Component } from 'react';
import axios from 'axios'

class NewsInCat extends Component {
    constructor(props) {
        super(props);
        this.state={News: []}
    }
    componentDidMount(){
        axios.get('http://localhost:4000/category/2/news')
        .then(res =>{
            this.setState({News: res.data.data})
        })
        .catch((error)=>{
                console.log("Error" + error)        
            
        })
    }
    render() {
        const {News} = this.state;
        return (
            <div className="container" style={{marginTop: 70}}>
                <header className="jumbotron my-4" style={{margin: "auto", display: "flex",alignItems: "center",  justifyContent: "center"}}>
        <h1 className="display-3">A Warm Welcome!</h1>
        <p className="lead">Lorem ipsum dolor sit amet, consectetur adipisicing elit</p>
      </header>

      <div className="row text-center">
          {News.map(item=>{
              const {title, short_intro, image} = item;
              return(
                <div className="col-lg-3 col-md-6 mb-4">
                <div className="card h-100">
                  <img className="card-img-top" style={{heigh}} src={image} alt="" />
                  <div className="card-body">
                    <h4 className="card-title">{title}</h4>
                    <p className="card-text">{short_intro}</p>
                  </div>
                </div>
              </div>
              )
          })}
      
            </div>
            </div>
        );
    }
}

export default NewsInCat;
