import React, { Component } from 'react';
import axios from 'axios';
import {BrowserRouter as NavLink} from 'react-router-dom';

class Nav extends Component {
    

    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state={Category: []}
      }

      handleClick(e){
        e.preventDefault();
        console.log("bbmbb")
      }
      componentDidMount() {
        axios.get('http://localhost:4000/category')
          .then(res => {
            this.setState({
                Category: res.data.data
            });
            console.log(res.data.data)
          })
          .catch((error) => {
            console.log(error);
          })
      }
    
    render() {
        const {Category} = this.state;
        return (
            <div style={{color:"white"}}>
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
                <div className="container">
                    <a className="navbar-brand">News </a>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarResponsive" aria-controls="navbarResponsive" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarResponsive">
                    <ul className="navbar-nav ml-auto">
                    {Category.map(item=>{
                        const {links, id, category_name} = item;
                         return(
                                <li className="nav-item" key={id} style={{width:90, textAlign:"center"}}>
                                    <NavLink to ={links} className="nav-link" onClick={this.handleClick}> {category_name}</NavLink>
                                </li>
                           
                        )

                    })}
                     </ul>                   
            </div>
        </div>
    </nav>
            </div>
        );
    }
}

export default Nav;
