import React, { Component } from 'react';
import axios from 'axios'

class Tag extends Component {
    constructor(props) {
        super(props);
        this.state={News: []}
    }
    componentDidMount(){
        axios.get('http://localhost:4000/news/2/tags')
        .then(res =>{
            this.setState({Tags: res.data})
        })
        .catch((error)=>{
                console.log("Error" + error)        
            
        })
    }
    render() {
        const {Tags} = this.state
        return (
            <div>
                {Tags.map((item, key) =>{
                    return(
                        <h1 className="mt-4">#{item.tag_name}</h1>
                    )
                })}
               
            </div>
        );
    }
}

export default Tag;
