import React, { Component } from 'react';
import axios from 'axios';
import {BrowserRouter as Link} from 'react-router-dom';

class Nav extends Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state={Category: []}
      }

      handleClick(e){
        e.preventDefault();
        console.log("bbmbb")
      }
      componentDidMount() {
        axios.get('http://localhost:4000/category')
          .then(res => {
            this.setState({
                Category: res.data.data
            });
            console.log(res.data.data)
          })
          .catch((error) => {
            console.log(error);
          })
      }
    
    render() {
        const {Category} = this.state;
        return (
            <div >
             {/* Navigation */}
        <nav className="navbar navbar-expand-lg navbar-light fixed-top" id="mainNav">
        <div className="container">
          <a className="navbar-brand js-scroll-trigger" href="#page-top">News</a>
          <button className="navbar-toggler navbar-toggler-right" type="button" data-toggle="collapse" data-target="#navbarResponsive" aria-controls="navbarResponsive" aria-expanded="false" aria-label="Toggle navigation">
            Menu
            <i className="fas fa-bars" />
          </button>
          <div className="collapse navbar-collapse" id="navbarResponsive">
            <ul className="navbar-nav ml-auto">              
              {Category.map((item, key)=>{
                        const {id, category_name} = item;
                         return(
                                <li className="nav-item" key={key} style={{width:90, textAlign:"center"}}>
                                    <Link to ='/category/"' className="nav-link js-scroll-trigger" onClick={this.handleClick}> {category_name}</Link>
                                </li>
                           
                        )

                    })}
            </ul>
          </div>
        </div>
      </nav>
            </div>
        );
    }
}

export default Nav;
