import React, { Component } from 'react';

class CreatePost extends Component {
    constructor(props) {
        super(props);
        this.onChangeCategoryName = this.onChangeCategoryName;

        this.state={
            categoryName: '',

        }
    }
    onChangeCategoryName(e){
        this.setState({
            categoryName: e.target.value
        })
    }
    onSubmit(e){
        e.preventDefault();
        console.log('Form submitted:');
        console.log('categoryName: ${this.state.categoryName}');

        this.setState({
            categoryName: '',
        })
        
    }

    

    render() {
        return (
            <div style={{marginTop: 40}}>
                <h3>Create News</h3>

                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Category Name*"
                            value={this.state.categoryName}
                            onChange={this.onChangeCategoryName} />
                    
                </div>
                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Title*"
                            value={this.state.title}
                            onChange={this.onChangeTitle} />
                    
                </div>
                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Desc*"
                            value={this.state.categoryName}
                            onChange={this.onChangeCategoryName} />
                    
                </div>
                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Category Name*"
                            value={this.state.categoryName}
                            onChange={this.onChangeCategoryName} />
                    
                </div>
                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Category Name*"
                            value={this.state.categoryName}
                            onChange={this.onChangeCategoryName} />
                    
                </div>
            </div>
        );
    }
}

export default CreatePost;
