import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
import Pagination from 'react-bootstrap/Pagination';
import PageItem from 'react-bootstrap/PageItem';
import NewsById from '../news/NewsById';

class NewsInCate extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Posts:[],
      currentPage:1,
      recordsPerPage:3,
    };
  }
  handleClick(e) {
    this.setState({
      currentPage: Number(e.target.id),
    });
  }
  componentDidMount(){
    axios.get('http://localhost:4000' +'/category/'+this.state.id+'/news')
      .then(res=>{
        this.setState({Posts: res.data.data})
      })
      .catch(function(error){
        console.log(error)
        
      })
  }

 
    render() {
      const {Posts, currentPage, recordsPerPage} = this.state;
      const indexOfLast = currentPage * recordsPerPage;
      const indexOfFirst = indexOfLast - recordsPerPage;
      const currentRecords =Posts.slice(indexOfFirst, indexOfLast);
  
      
  
      const pageNumbers = [];
      for (let i = 1; i <= Math.ceil(Posts.length / recordsPerPage); i++) {
        pageNumbers.push(i);
      }
  
      const renderPageNumbers = pageNumbers.map((number) => {
        return (
          <PageItem key={number} id={number} active={this.state.currentPage === number} onClick={this.handleClick}>{number}</PageItem>
        );
      });
      
        return (
            <div style={{margin: 80}}>
                {/* Heading Row */}
      <div className="row align-items-center my-5">
        <div className="col-lg-7">
          <img className="img-fluid rounded mb-4 mb-lg-0" src="http://placehold.it/900x400" alt="" />
        </div>
        {/* /.col-lg-8 */}
        <div className="col-lg-5">
          <h1 className="font-weight-light">Business Name or Tagline</h1>
          <p>This is a template that is great for small businesses. It doesn't have too much fancy flare to it, but it makes a great use of the standard Bootstrap core components. Feel free to use this template for any project you want!</p>
          <a className="btn btn-primary" href="#">Read More </a>
        </div>
      </div>
        <ul>
      {Posts.map(item=>{
        const {title, short_intro, links} = item;
        return (
          <li>
            {/* Content Row */}
             <div className="row">
              <div className="col-md-4 mb-5">
                  <div className="card h-100">
                     <Link to={links} >
       <div className="card-body">
         <h2 className="card-title">{title}</h2>
         <p className="card-text">{short_intro}</p>
       </div>
       </Link>

       <div className="card-footer">
         <a href="#" className="btn btn-primary btn-sm">Read More </a>
       </div>
     </div>
   </div>
 </div>
 </li>
        )
      })}
   </ul>
      <Pagination className="pagination" size="md">
        {renderPageNumbers}
      </Pagination>

            </div>
        );
    }
}

export default NewsInCate;
