import React, { Component } from 'react';

class CreateCom extends Component {
    constructor(props) {
        super(props);
        this.onChangeUserName = this.onChangeUserName.bind(this);
        this.onChangeComment = this.onChangeComment.bind(this);

        this.state ={
            userName: '',
            comment: ''
        }
    }
    onChangeUserName(){
        this.setState({
            userName:''
        })
    }
    onChangeComment(){
        this.setState({
            commment:''
        })
    }
    onSubmit(e){
        e.preventDefault;
        this.setState({
            userName: '',
            comment: ''
        })
    }
    
    render() {
        return (
            <div>
            <div className="form-group">
                <input type="text" className="form-control" 
                        placeholder aria-describedby="Enter UserName"
                        onChange={this.onChangeUserName} />
            </div>

             <div className="form-group">
                <input type="text" className="form-control" 
                        placeholder aria-describedby="Enter comment"
                        onChange={this.onChangeComment} />
            </div>
            <button type="submit" className="btn btn-primary" onSu>Submit</button>
            </div>
        );
    }
}

export default CreateCom;
