import React, { Component } from 'react';
import axios from 'axios'

import ListCom from './ListCom';
import Tag from './Tag';
import RelatedNews from './RelatedNews';

class OneNews extends Component {
    constructor(props) {
        super(props);
        this.state={News: []}
    }
    componentDidMount(){
        axios.get(`http://localhost:4000/news/${this.props.match.params.id}`)
        .then(res =>{
            this.setState({News: res.data})
            console.log(res.data)
        })
        .catch((error)=>{
                console.log("Error" + error)        
            
        })
    }
    formatDate = (date)=>{
        date = date.substring(11, 16)+' '+date.substring(8, 10)+'/'+date.substring(5, 7)+'/'+date.substring(0, 4);
                console.log(date);
    }
    render() {
        const {News} =this.state;
        
        return (
            <div style={{margin: 70}}>
      <div className="container" >
        <div className="row" >
        
            {News.map((item, key) =>{
                const {title, content, short_intro, image, date_created, author_name} = item;
                
                

                //const {date} = date_created.getDate() + "/"+ parseInt(date_created.getMonth()+1) +"/"+date_created.getFullYear();
                
                return(
          <div className="col-lg-8"  key={key}>
            <h1 className="mt-4">{title}</h1>
            <p className="lead">
              by
              <a> {author_name}</a>
            </p>
            <hr />
            <p>{(date_created) =this.formatDate()}</p>
            <hr />
            <img className="img-fluid rounded" src={image} alt="" />
            <hr />
            <p className="lead" style={{fontStyle: "italic", fontSize: 15}}>{short_intro}</p> <hr />
            <p className="lead">{content}</p> <hr />
            
            </div>
                )

            })}
            
        </div>   
        <Tag news_id={this.props.match.params.id}></Tag>
        <hr/>
        <RelatedNews news_id={this.props.match.params.id}></RelatedNews>
        <ListCom news_id={this.props.match.params.id}></ListCom>
        </div>   

        </div>
        );
    }
}

export default OneNews;
