import React, { Component } from 'react';
import axios from 'axios'

class RelatedNews extends Component {
    constructor(props) {
        super(props);
        this.state={News: []}
    }
    componentDidMount(){
        axios.get('http://localhost:4000/category/2/news')
        .then(res =>{
            this.setState({News: res.data.data})
        })
        .catch((error)=>{
                console.log("Error" + error)        
            
        })
    }
    render() {
        const {News} = this.state;
        return (
            <div className="container" style={{marginTop: 70}}>
                     <div className="row text-center">
          {News.map(item=>{
              const {title, short_intro, image} = item;
              return(
                <div className="col-lg-3 col-md-6 mb-4">
                <div className="card h-100">
                  <img className="card-img-top" style={{height: 170}} src={image} alt="" />
                  <div className="card-body">
                    <h4 className="card-title" style={{fontSize: 10, marginBottom: 0, padding: 10}}>{title}</h4>
                  </div>
                </div>
              </div>
              )
          })}
      
            </div>
            </div>
        )}

}

export default RelatedNews;
