import React, { Component } from 'react';

class CreatePost extends Component {
    constructor(props) {
        super(props);
        this.onChangeCategoryName = this.onChangeCategoryName;
        this.onChangeTitle = this.onChangeTitle;
        this.onChangeShort_intro = this.onChangeShort_intro;
        this.onChangeAuthor = this.onChangeAuthor;
        this.onChangeContent = this.onChangeContent;

        this.state={
            categoryName: '',
            title: '',
            short_intro: '',
            author: '',
            content: ''
        }
    }
    onChangeCategoryName(e){
        this.setState({
            categoryName: e.target.value
        })
    }
    onChangeTitle(e){
        this.setState({
            title: e.target.value
        })
    }
    onChangeShort_intro(e){
        this.setState({
            short_intro: e.target.value
        })
    }
    onChangeAuthor(e){
        this.setState({
            categoryName: e.target.value
        })
    }
    onChangeCategoryName(e){
        this.setState({
            categoryName: e.target.value
        })
    }
    onSubmit(e){
        e.preventDefault();
        console.log('Form submitted:');
        console.log('categoryName: ${this.state.categoryName}');

        this.setState({
            categoryName: '',
        })
        
    }

    

    render() {
        return (
            <div style={{marginTop: 40}}>
                <h3>Create News</h3>

                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Category Name*"
                            value={this.state.categoryName}
                            onChange={this.onChangeCategoryName} />
                    
                </div>
                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Title*"
                            value={this.state.title}
                            onChange={this.onChangeTitle} />
                    
                </div>
                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Description*"
                            value={this.state.short_intro}
                            onChange={this.onChangeShort_intro} />
                    
                </div>
                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Author*"
                            value={this.state.author}
                            onChange={this.onChangeAuthor} />
                    
                </div>
                <div className="form-group">
                    <label htmlFor />
                    <input type="text" 
                            className="form-control" 
                            placeholder aria-describedby="Content*"
                            value={this.state.content}
                            onChange={this.onChangeContent} />
                    
                </div>
            </div>
        );
    }
}

export default CreatePost;
