import React, { Component } from 'react';
import axios from 'axios';

import Listcom from './ListCom';

class NewsById extends Component {
    constructor(props) {
        super(props);
        this.state={Post: []}
      }
      componentDidMount(){
        axios.get('http://localhost:4000/category/2/news')
          .then(res=>{
            this.setState({Post: res.data})
          })
          .catch(function(err){
            console.log(err); 
          })
      }
    render() {
        const {Post} = this.state;
        return (
            <div className="container" style={{margin: 80}}>
            { Post.map(item =>{
              return(
                <div className="row">
                {/* Post Content Column */}
                <div className="col-lg-8">
      
                {/*const {title, short_intro, author_name, date_created, cate }*/} 
                  {/* Title */}
                  <h1 className="mt-4">{item.title}</h1>
                  {/* Author */}
                  <p className="lead">
                    by
                    <a href="#"> {item.author_name}</a>
                  </p>
                  <hr />
                  {/* Date/Time */}
                  <p>{item.date_created}</p>
                  <hr />
                  {/* Preview Image */}
                  <img className="img-fluid rounded" src="http://placehold.it/900x300" alt="" />
                  <hr/>
                  <p className="lead">{item.content}</p>
                             
                   <hr />
                </div>
              </div>
              )
                
              })
            }
      
    <Listcom></Listcom>
    
    {/* Sidebar Widgets Column */}
    <div className="col-md-4">
      {/* Search Widget */}
      <div className="card my-4">
        <h5 className="card-header">Search</h5>
        <div className="card-body">
          <div className="input-group">
            <input type="text" className="form-control" placeholder="Search for..." />
            <span className="input-group-btn">
              <button className="btn btn-secondary" type="button">Go!</button>
            </span>
          </div>
        </div>
      </div>
    </div>
    
    </div>
        );
    }
}

export default NewsById;
