import React, { Component } from 'react';
import axios from 'axios';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state={
            News:[]
        }
    }
    componentDidMount(){
        axios.get('http://localhost:4000/news')
          .then(res => {
            this.setState({
                News: res.data.data
            });
            console.log(res.data.data)
          })
          .catch((error) => {
            console.log(error);
    })
}

    
    render() {
        const {News} = this.state
        return(
            <div style={{margin: 80}}>

<header>
        <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
          <ol className="carousel-indicators">
            <li data-target="#carouselExampleIndicators" data-slide-to={0} className="active" />
            <li data-target="#carouselExampleIndicators" data-slide-to={1} />
            <li data-target="#carouselExampleIndicators" data-slide-to={2} />
          </ol>
          <div className="carousel-inner" role="listbox">
            {/* Slide One - Set the background image for this slide in the line below */}
            <div className="carousel-item active" style={{backgroundImage: 'url("http://placehold.it/1900x1080")'}}>
              <div className="carousel-caption d-none d-md-block">
                <h3>First Slide</h3>
                <p>This is a description for the first slide.</p>
              </div>
            </div>
            {/* Slide Two - Set the background image for this slide in the line below */}
            <div className="carousel-item" style={{backgroundImage: 'url("http://placehold.it/1900x1080")'}}>
              <div className="carousel-caption d-none d-md-block">
                <h3>Second Slide</h3>
                <p>This is a description for the second slide.</p>
              </div>
            </div>
            {/* Slide Three - Set the background image for this slide in the line below */}
            <div className="carousel-item" style={{backgroundImage: 'url("http://placehold.it/1900x1080")'}}>
              <div className="carousel-caption d-none d-md-block">
                <h3>Third Slide</h3>
                <p>This is a description for the third slide.</p>
              </div>
            </div>
          </div>
          <a className="carousel-control-prev" href="#carouselExampleIndicators" role="button" data-slide="prev">
            <span className="carousel-control-prev-icon" aria-hidden="true" />
            <span className="sr-only">Previous</span>
          </a>
          <a className="carousel-control-next" href="#carouselExampleIndicators" role="button" data-slide="next">
            <span className="carousel-control-next-icon" aria-hidden="true" />
            <span className="sr-only">Next</span>
          </a>
        </div>
      </header>
                 { News.map(item=>{
                return (
                    <div className="row" key={item.id}>
                    <div className="col-md-4 mb-5">
                        <div className="card h-100">
                          <a href={item.links}> 
                          <div className="card-body">
                                  <img className="card-img-top" src={item.image} alt="" />
                                  <h2 className="card-title">{item.title}</h2>
                                  <p className="card-text">{item.short_intro}</p>
                              </div>
                          </a>
                          
                    </div>
                   </div>
                  </div>                
                );
            })}
            </div>
 )}
}

export default Home;
