import React, { Component } from 'react';
import axios from 'axios';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state={
            News:[]
        }
    }
    componentDidMount(){
        axios.get('http://localhost:4000/news')
          .then(res => {
            this.setState({
                News: res.data.data
            });
            console.log(res.data.data)
          })
          .catch((error) => {
            console.log(error);
    })
}

    
    render() {
        const {News} = this.state
        return(
            News.map(item=>{
                return (
                    <div>
                <div className="card-deck">
                  <div className="card">
                    <img className="card-img-top" src={item.image} alt="" />
                    <div className="card-body" href={item.links}>
                      <h4 className="card-title">{item.title}</h4>
                      <p className="card-text">{item.short_intro}</p>
                    </div>
                  </div>
                </div>
              </div>
                );
            })
        )
      
       
    }
}

export default Home;
