import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';

import Listcom from '../comment/Listcom';
import CreCom from '../comment/Createcom'

class PostById extends Component {
  constructor(props) {
    super(props);
    this.state={Posts: []}
  }
  componentDidMount(){
    axios.get('http://localhost:4000/:id')
      .then(res=>{
        this.setState({Posts: res.data})
      })
      .catch(function(err){
        console.log(err); 
      })
  }
  
    render() {
     // const {Post } =this.state;
        return (
            <div className="container" style={{margin: 80}}>
        <div className="row">
          {/* Post Content Column */}
          <div className="col-lg-8">

          {/*const {title, short_intro, author_name, date_created, cate }*/} 
            {/* Title */}
            <h1 className="mt-4">{this.state.title}</h1>
            {/* Author */}
            <p className="lead">
              by
              <a href="#">{this.state.author_name}</a>
            </p>
            <hr />
            {/* Date/Time */}
            <p>{this.state.date_created}</p>
            <hr />
            {/* Preview Image */}
            <img className="img-fluid rounded" src="http://placehold.it/900x300" alt="" />
            <hr />
            {/* Post Content */}
            <p>{this.state.short_intro}</p>
            <hr/>
            <p className="lead">{this.state.content}</p>
                       
             <hr />
          </div>
        </div>
      <Listcom></Listcom>
      
      {/* Sidebar Widgets Column */}
      <div className="col-md-4">
        {/* Search Widget */}
        <div className="card my-4">
          <h5 className="card-header">Search</h5>
          <div className="card-body">
            <div className="input-group">
              <input type="text" className="form-control" placeholder="Search for..." />
              <span className="input-group-btn">
                <button className="btn btn-secondary" type="button">Go!</button>
              </span>
            </div>
          </div>
        </div>
      </div>
      
      </div>
        );
    }
}

export default PostById;
